import React, { useEffect, useState } from "react";
import "./styles/App.css";

const App = () => {
  const [weatherData, setWeatherData] = useState([]);
  const [hotelsData, setHotelsData] = useState([]);
  const [placesData, setPlacesData] = useState([]);

  useEffect(() => {
    // Fetch weather data
    fetch("http://localhost:5000/weather")
      .then((res) => res.json())
      .then((data) => setWeatherData(data))
      .catch((err) => console.error("Error fetching weather data:", err));

    // Fetch hotels data
    fetch("http://localhost:5000/hotels")
      .then((res) => res.json())
      .then((data) => setHotelsData(data))
      .catch((err) => console.error("Error fetching hotels data:", err));

    // Fetch places data
    fetch("http://localhost:5000/places")
      .then((res) => res.json())
      .then((data) => setPlacesData(data))
      .catch((err) => console.error("Error fetching places data:", err));
  }, []);

  return (
    <div className="app">
      <header className="app-header">
        <h1>Travel Dashboard</h1>
      </header>

      {/* Weather Data */}
      <section className="card">
        <h2>Weather Data</h2>
        <table>
          <thead>
            <tr>
              <th>City</th>
              <th>Temperature</th>
              <th>Condition</th>
            </tr>
          </thead>
          <tbody>
            {Object.entries(weatherData).map(([city, details]) => (
              <tr key={city}>
                <td>{city}</td>
                <td>{details.temperature}</td>
                <td>{details.condition}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </section>

      {/* Hotels Data */}
      <section className="card">
        <h2>Top Hotels</h2>
        <table>
          <thead>
            <tr>
              <th>City</th>
              <th>Hotel Name</th>
              <th>Rating</th>
              <th>Address</th>
            </tr>
          </thead>
          <tbody>
            {Object.entries(hotelsData).map(([city, hotels]) =>
              hotels.map((hotel, index) => (
                <tr key={`${city}-${index}`}>
                  <td>{city}</td>
                  <td>{hotel.name}</td>
                  <td>{hotel.rating}</td>
                  <td>{hotel.address}</td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </section>

      {/* Places Data */}
      <section className="card">
        <h2>Top Places to Visit</h2>
        <table>
          <thead>
            <tr>
              <th>City</th>
              <th>Place Name</th>
              <th>Rating</th>
              <th>Address</th>
            </tr>
          </thead>
          <tbody>
            {Object.entries(placesData).map(([city, places]) =>
              places.map((place, index) => (
                <tr key={`${city}-${index}`}>
                  <td>{city}</td>
                  <td>{place.name}</td>
                  <td>{place.rating}</td>
                  <td>{place.address}</td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </section>
    </div>
  );
};

const VisualizationApp = () => {
  const [showImage, setShowImage] = useState(false);

  // Path to your local image (place it in the "public" folder for easy access)
  const imagePath = process.env.PUBLIC_URL + "/image.png";

  const handleShowImage = () => {
    setShowImage(true);
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Scraped Data Visualization</h1>

        {/* Button to Show Image */}
        <button onClick={handleShowImage} className="show-button">
          Show Report Image
        </button>

        {/* Conditionally Render the Image */}
        {showImage && (
          <div className="image-container">
            <img
              src={imagePath}
              alt="Report Visualization"
              style={{
                width: "100%",
                maxWidth: "800px",
                marginTop: "20px",
                border: "1px solid #ccc",
                borderRadius: "5px",
              }}
            />
          </div>
        )}
      </header>
    </div>
  );
};

export default App;
